import React, { useState } from "react";
import './Homepage.css';
import TrackActivity from "../components/TrackActivity";
import Workout from "../components/Workout";
import Calories from "../components/Calories";
import Steps from "../components/Steps";

const Homepage = () => {
  const [ left_menu, setLeftMenu ] = useState("Overview")
  const [transition, setTransition] = useState(false);

  const handleClick = (menu) => {
      if (left_menu !== menu) {
          setTransition(true);
          setTimeout(() => {
              setLeftMenu(menu);
              setTransition(false);
          }, 300); // Duration of the transition in milliseconds
      }
  };

  return (
    <div>
      <div className="left-menu-bar">
        <div className="fitness-home-icon">
          <h2>Fitness</h2>
          <i className="fa-solid fa-dumbbell" style={{ color: "#fb7316", fontSize: "1.5rem" }} />
        </div>
        <hr/>
        <div className="menu-options">
            <ul>
                <li onClick={ () => setLeftMenu('Overview') }><i class="fa-solid fa-vector-square"></i><button className={left_menu === 'Overview' ? 'active' : ''}>Overview</button></li>
                <li onClick={ () => setLeftMenu('Workout') }><i class="fa-solid fa-dumbbell"></i><button className={`${left_menu === 'Workout' ? 'active' : ''} $${transition ? 'transition' : ''}`}>Workout</button></li>
                <li onClick={ () => setLeftMenu('Diet Plan') }><i class="fa-solid fa-cookie-bite"></i><button className={left_menu === 'Diet Plan' ? 'active' : ''}>Diet Plan</button></li>
                {/* <li><button>Goals</button></li>
                <li><button>My Schedule</button></li>
                <li><button>Progress</button></li> */}
            </ul>
        </div>
        <hr/>
        <div className="loggout">
            <i className="fa-solid fa-angles-left"></i>
            <button>Loggout</button>
        </div>
      </div>
      <div className="navbar">
        <div className="search-bar"></div>
        {/* <ul>
          <li>Noti</li>
          <li>Setting</li>
          <li>Icon</li>
        </ul> */}
      </div>
      <div className="menu">
        <div className="info-page">
          {/* <TrackActivity />
          <Workout />
          <Calories />
          <Steps /> */}
          <div className="goal-process"></div>
        </div>
        <div className="schedule-page"></div>
      </div>
    </div>
  );
};

export default Homepage;
