import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Health.css";

const Health = () => {
  const [userId, setUserId] = useState("");
  const [userName, setUserName] = useState("");
  const navigate = useNavigate();

  const features = [
    {
      type: "Weight",
      value: "75",
      unit: "kg",
      time: "2025/13/32",
      icon: "fa-weight-scale",
    },
    {
      type: "Sleep",
      value: ["7", "30"],
      unit: ["h", "min"],
      time: "2026/14",
      icon: "fa-moon",
    },
    {
      type: "Steps",
      value: "123,456",
      unit: "steps",
      time: "2027/15/33",
      icon: "fa-shoe-prints",
    },
    {
      type: "Calories",
      value: "20,000",
      unit: "cals",
      time: "2028/16",
      icon: "fa-fire",
    },
  ];

  const featuresComponent = features.map((item) => {
    <div className="summary-component">
      <div className="component-type">
        <i className={`fa-solid ${item.icon}`} />
        <p>{item.type}</p>
      </div>
      <div className="top-right">
        <p>{item.time}</p>
        <i className="fa-solid fa-angle-right"></i>
      </div>
      <div className="value">
        {item.type === 'Sleep' ? 
          <div className='sleep-value'>
            <p id='number'>{item.value[0]}</p>
          </div> :
          <div>
            </div>}
      </div>
    </div>;
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        if (token) {
          const response = await axios.get("http://localhost:5001/homepage", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          const data = response.data;
          console.log(data);
          setUserId(data._id);
          setUserName(data.username);
        } else {
          console.error("No token found");
        }
      } catch (error) {
        console.error("There was an error fetching the data!", error);
      }
    };

    fetchUser();
  }, []);

  return (
    <div className="health-overview">
      <div className="health-header">
        <h2>{userName}'s Health Summary</h2>
        <hr />
      </div>
      <div className="health-middle-section">
        <div id="add-features"></div>
        <div className="summary"></div>
      </div>
    </div>
  );
};

export default Health;
