import { useState } from "react";
import { Link } from "react-router-dom";
import "./Login.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errors, setErrors] = useState({});

  async function onSubmitHandler(e) {
    e.preventDefault();
    const validationErrors = {};

    if (!email.trim()) {
      validationErrors.email = "Email is required!";
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      validationErrors.email = "Email is NOT valid!";
    }

    if (!password.trim()) {
      validationErrors.password = "Password is required!";
    }
    setError
    const apiUrl = "http://localhost:5001/login";
    try {
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const data = await response.json();
        if (data.message.includes("Email already registered.")) {
          validationErrors.dup_email = "Email already registered.";
          setErrors({...validationErrors});
        }
        return;
      }
      navigate("/login");



    } catch (err) {
      console.log("Error: " + err.message);
    }
  }

  return (
    <div className="background-image">
      <div className="auth-container">
        <div className="auth-form-container">
          <h2 className="auth-title">Login</h2>
          <form action="/auth" method="POST" onSubmit={onSubmitHandler}>
            <label className="auth-text">Email</label>
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <label className="auth-text">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Link className="auth-link" style={{ textDecoration: "none" }} to="/homepage">
              <button className="auth-submit-btn">Submit</button>
            </Link>
            <label className="signup">
              Don't have an account?{" "}
              <Link
                className="link"
                style={{ textDecoration: "none" }}
                to="/signup"
              >
                <p>Sign Up</p>
              </Link>
            </label>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
