import React, { useState } from "react";
import "./User.css";

const user_info = [
  { type: 'First Name', value: "firstName", method: "setFirstName" },
  { type: 'Last Name', value: "lastName", method: "setLastName" },
  { type: 'Gender', value: 'gender', method: "setGender" },
  { type: 'Phone Number', value: "phoneNumber", method: "setPhoneNumber" }, 
  { type: 'Address', value: 'address'}
];




const User = () => {
  const [firstName, setFirstName] = useState(null);
  const [lastName, setLastName] = useState(null);
  const [phoneNumber, setPhoneNumber] = useState(null);
  const [gender, setGender] = useState(null);
  const [address, setAddress] = useState(null);



  return (
    <div className="userpage">
      <div className="user-navbar">
        <i className="fa-solid fa-arrow-left" />
        <p>User Profile</p>
      </div>
      <div className="middle-section">
        <div className="left-section">
          <div className="image-icon">
            {/* image */}
            {/* name */}
            <p> Edit Profile </p>
          </div>
        </div>
        <div className="right-section">
          <form action="/update-user" method="PUT">
            <label className="user-text">First Name</label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            />
            <label className="user-text">Last Name</label>
            <input
              type="text"
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            />
            <label className="user-text">Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            {errors.password && <span>{errors.password}</span>}
            <button className="update-user-submit-btn">Submit</button>

            <label className="signup">
              Don't have an account?{" "}
              <Link
                className="link"
                style={{ textDecoration: "none" }}
                to="/signup"
              >
                <p>Sign Up</p>
              </Link>
            </label>
          </form>
        </div>
      </div>
    </div>
  );
};

export default User;
