import React, { useState } from 'react';
import './DataTable.css';
import AddBoxOutlinedIcon from '@mui/icons-material/AddBoxOutlined';

const DataTable = () => {
  // Initialize the state with 5 rows and 5 columns of empty strings
  const [row, setRow] = useState(4);
  const [col, setCol] = useState(4);
  const [data, setData] = useState(() => {
    const rows = new Array(row).fill(null).map(() => new Array(col).fill(''));
    rows[0][0] = 'Actions';
    rows[0][1] = 'Set 1';
    rows[0][2] = 'Set 2';
    return rows;
  })

  // Handle input change
  const handleInputChange = (value, row, col) => {
    // Update the specific cell with the new value
    const updatedData = data.map((rowData, rowIndex) => {
      if (rowIndex === row) {
        return rowData.map((cellData, colIndex) => {
          if (colIndex === col) {
            return value;
          }
          return cellData;
        });
      }
      return rowData;
    });

    setData(updatedData);
  };

  return (
    <table className="workout-table">
      <tbody>
        {data.map((row, rowIndex) => (
          <tr key={rowIndex}>
            {row.map((cell, colIndex) => (
              <td key={colIndex}>
                {rowIndex === 0 && colIndex === col - 1 ? (
                    <AddBoxOutlinedIcon />
                ) : (



                <input
                  className='input-box'
                  type="text"
                  value={cell}
                  readOnly={rowIndex === 0}
                  onChange={(e) => handleInputChange(e.target.value, rowIndex, colIndex)}
                />)}
              </td>
            ))}
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default DataTable;
