import React, { useEffect, useState } from "react";
import axios from "axios";
import "./DataTable.css";
import AddBoxOutlinedIcon from "@mui/icons-material/AddBoxOutlined";

const DataTable = ({
  date,
  numRow,
  setNumRow,
  numCol,
  setNumCol,
  data,
  setData,
}) => {
  const [userId, setUserId] = useState("");

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        if (token) {
          const response = await axios.get("http://localhost:5001/homepage", {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          });

          // console.log("Response Username ", response);
          const dataTableIndex = response.data.dataTables.findIndex(
            (workout) => workout.workoutDate === date
          );
          // console.log(dataTableIndex);

          const dataTable = response.data.dataTables[dataTableIndex];
          // console.log("DT: ", dataTable);
          setUserId(response.data._id);
          if (dataTable) {
            setNumRow(dataTable.numRow);
            setNumCol(dataTable.numCol);
          }

          setData(() => {
            const rows = new Array(numRow)
              .fill(null)
              .map(() => new Array(numCol).fill(""));
            rows[0][0] = "Actions";

            for (let i = 1; i < numCol; i++) {
              rows[0][i] = `Set ${i}`;
            }
            // console.log(dataTable.data);
            // console.log(dataTable.data[0]);
            // console.log(dataTable.data[0].action);
            // console.log(dataTable.data[0].sets[1]);
            if (dataTable.data) {
              for (let row = 1; row < numRow - 1; row++) {
                rows[row][0] = dataTable.data[row - 1].action;
                // console.log(dataTable.data[row].action);
                for (let col = 1; col < numCol; col++) {
                  rows[row][col] = dataTable.data[row - 1].sets[col - 1];
                }
              }
            }

            // console.log(rows);
            return rows;
          });

          // console.log(data);
        } else {
          console.error("No token found");
        }
      } catch (error) {
        console.error("There was an error fetching the data!", error);
      }
    };
    fetchUser();
  });


  useEffect(() => {
    setData(() => {

      const dtRowLen = data.length + 1;
      const dtColLen = data[0].length;
      const diffRow = numRow - dtRowLen;
      const diffCol = numCol - dtColLen;

      console.log(data);
      console.log(data[0]);
      console.log("Num Row: " + numRow);
      console.log("Num Col: " + numCol);
      console.log()
      console.log("Diff Row: " + diffRow);
      console.log("Diff Col: " + diffCol);
      console.log('');
      const rows = data.map((row) => row.slice());
      if (diffRow > 0) {
        rows.forEach((row, index) => {
          for (let i = 0; i < diffCol; i++) {
            row.push(index === 0 ? `Set${data[0].length - 1 + i}` : ' ');
          }
        });
      }

      if (diffCol > 0) {
        for (let i = 0; i < diffRow; i++) {
          rows.push(new Array(numCol).fill(""));
        }
      }
      return rows;
    });
  }, [numRow, numCol])
  // Handle input change
  const handleInputChange = (value, row, col) => {
    // Update the specific cell with the new value
    const updatedData = data.map((rowData, rowIndex) => {
      if (rowIndex === row) {
        return rowData.map((cellData, colIndex) => {
          if (colIndex === col) {
            return value;
          }
          return cellData;
        });
      }
      return rowData;
    });

    setData(updatedData);
  };

  const updateTable = (rowIndex, colIndex) => {
    if (rowIndex === 0) {
      setNumCol(numCol + 1);
    } else if (colIndex === 0) {
      setNumRow(numRow + 1);
    }
  };

  return (
    <div className="workout-table-container">
      <table className={`workout-table${numCol > 4 ? " scroll" : ""}`}>
        <tbody>
          {data.map((row, rowIndex) => (
            <tr key={rowIndex}>
              {row.map((cell, colIndex) => (
                <td
                  key={colIndex}
                  style={{
                    padding: "0",
                    height: rowIndex === numRow - 1 ? "0.5em" : "3em",
                    backgroundColor:
                      rowIndex === numRow - 1 || colIndex === numCol - 1
                        ? "#F0F3F8"
                        : "",
                  }}
                >
                  {(rowIndex === 0 && colIndex === numCol - 1) ||
                  (rowIndex === numRow - 1 && colIndex === 0) ? (
                    <div className="add">
                      <div
                        className="add-container"
                        onClick={() => updateTable(rowIndex, colIndex)}
                      >
                        <AddBoxOutlinedIcon
                          style={{
                            transform: "scale(0.9)",
                            margin: "2px",
                          }}
                        />
                        {rowIndex === numRow - 1 && <p>New</p>}
                      </div>
                    </div>
                  ) : (
                    <input
                      className="input-box"
                      type="text"
                      value={cell}
                      readOnly={
                        rowIndex === 0 ||
                        colIndex === numCol - 1 ||
                        rowIndex === numRow - 1
                      }
                      onChange={(e) =>
                        handleInputChange(e.target.value, rowIndex, colIndex)
                      }
                      style={{
                        width:
                          colIndex === numCol - 1
                            ? "3em"
                            : colIndex === 0
                            ? "15em"
                            : "12em",
                        height: rowIndex === numRow - 1 ? "0.5em" : "2em",
                        backgroundColor:
                          rowIndex === numRow - 1 || colIndex === numCol - 1
                            ? "#F0F3F8"
                            : "",
                      }}
                    />
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataTable;
