import { useState } from "react";
import { Link } from "react-router-dom";
import "./Signup.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function onSubmitHandler(e) {
    e.preventDefault();
    const apiUrl = "http://localhost:5001/signup";

    // Make a GET request
    fetch(apiUrl, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ email, username, password }),
    })
      .then((response) => {
        if (response.ok) alert("SIGNED IN!");
        return response.json();
      })
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  return (
    <div className="auth-container">
      <div className="auth-form-container">
        <h2 className="auth-title">Sign Up</h2>
        <form action="/auth" method="POST" onSubmit={onSubmitHandler}>
          <label className="auth-text">Email</label>
          <input
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label className="auth-text">Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className="auth-text">Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button className="auth-submit-btn">Submit</button>
          <label className="signup">
            Don't have an account?{" "}
            <Link class="link" style={{ textDecoration: "none" }} to="/signup">
              <p>Sign Up</p>
            </Link>
          </label>
        </form>
      </div>
    </div>
  );
};

export default Login;
