import React, { useState } from "react";
import "./User.css";
import ImageUpload from "../components/ImageUpload";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";

const User = () => {
  const [firstName, setFirstName] = useState(null);
  const [lastName, setLastName] = useState(null);
  const [phoneNumber, setPhoneNumber] = useState(null);
  const [gender, setGender] = useState(null);
  const [address, setAddress] = useState(null);

  const updateGender = (event) => {
    setGender(event.target.value);
  };

  return (
    <div className="userpage">
      <div className="user-navbar">
        <i className="fa-solid fa-arrow-left-long" />
        <p>User Profile</p>
      </div>
      <div className="middle-section">
        <div className="left-section">
          <ImageUpload />
          <p className="username">Derrrr Zhu</p>
          <p classname="email">der@gmail.com</p>
          <button>Edit Profile</button>
        </div>
        <div className="right-section">
          <p id="user-profile">Derrr's Profile </p>
          <form action="/update-user" method="PUT">
            {/* {user_components} */}
            <Box
              component="form"
              sx={{
                "& .MuiTextField-root": { m: 1, width: "25ch" },
              }}
              id="user-box"
              noValidate
              autoComplete="off"
              style={{ padding: "1em 1.5em" }}
            >
              <TextField
                required
                id="outlined-required"
                label="First Name"
                defaultValue=""
                style={{ width: "15rem" }}
              />
              <TextField
                required
                id="outlined-required"
                label="Last Name"
                defaultValue=""
                style={{ width: "15rem" }}
              />
              <TextField
                required
                id="outlined-required"
                label="Phone Number"
                defaultValue=""
                style={{ width: "15rem" }}
              />
              <TextField
                required
                id="outlined-required"
                label="Address"
                defaultValue=""
                style={{ width: "15rem" }}
              />
            </Box>

            <FormControl sx={{ m: 1, minWidth: 80 }}>
              <InputLabel id="demo-simple-select-standard-label">
                Gender
              </InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={gender}
                onChange={updateGender}
                label="Gender"
                style={{ width: "15rem" }}
              >
                <MenuItem value={"Male"}>Male</MenuItem>
                <MenuItem value={"Female"}>Female</MenuItem>
                <MenuItem value={"Other"}>Other</MenuItem>
              </Select>
            </FormControl>

            <button className="update-user-submit-btn">Submit</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default User;
