import React from "react";
import "./UpdateComp.css";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import ListItemText from "@mui/material/ListItemText";
import Select from "@mui/material/Select";
import Checkbox from "@mui/material/Checkbox";

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const types = ["Weight", "Sleep", "Calories", 'Steps', 'Height'];

const UpdateComp = ({ formVisibility, setFormVisibility }) => {
  const [type, setType] = React.useState([]);

  const handleChange = (event) => {
    const {
      target: { value },
    } = event;
    setType(
      // On autofill we get a stringified value.
      typeof value === "string" ? value.split(",") : value
    );
  };

  return (
    <div
      className="update-comp"
      style={{ visibility: !formVisibility ? "hidden" : "visible" }}
    >
      <div className={`slideBox ${!formVisibility ? '': 'active'}`}>
        <FormControl sx={{ m: 1, width: 300, color: 'white', '.MuiInputLabel-root': { color: 'white' }, '.MuiOutlinedInput-root': { color: 'white', '& fieldset': { borderColor: 'white' } } }}>
          <InputLabel id="demo-multiple-checkbox-label">Choose Your Favorites</InputLabel>
          <Select
            labelId="demo-multiple-checkbox-label"
            id="demo-multiple-checkbox"
            multiple
            value={type}
            onChange={handleChange}
            input={<OutlinedInput label="Choose Your Favorites" />}
            renderValue={(selected) => selected.join(", ")}
            MenuProps={MenuProps}
          >
            {types.map((t) => (
              <MenuItem key={t} value={t}>
                <Checkbox checked={type.indexOf(t) > -1} />
                <ListItemText primary={t} />
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        <div className="update-btn-container">
        <button className="update-btn" onClick={updateFavoriteComponent}>
          <DoneAllTwoToneIcon />
          <p>Update</p>
        </button>
        {<p id='submit-message'>{submitMessage}</p>}
      </div>
      </div>
    </div>
  );
};

export default UpdateComp;
